import React, { useState, useRef } from "react";
import { Modal, Upload, Button } from "antd";
import {
  PlusOutlined,
  CameraOutlined,
  FolderAddFilled,
} from "@ant-design/icons";
import { slugify } from "../helpers/string-helper";

function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });
}

const UploadPhotos = ({
  photoList,
  handelPhotoListChange,
  category,
  multiple,
}) => {
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewImage, setPreviewImage] = useState("");
  const [previewTitle, setPreviewTitle] = useState("");
  const [selectedPhotos, setSelectedPhotos] = useState([]);
  const cameraInputRef = useRef(null);
  const [showUploadOptions, setShowUploadOptions] = useState(false);

  var showUpload = true;
  if (!multiple) {
    if (photoList.length > 0) {
      showUpload = false;
    }
  }

  const handleCancel = () => {
    setPreviewVisible(false);
    setSelectedPhotos([]);
  };

  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }
    setPreviewImage(file.url || file.preview);
    setPreviewVisible(true);
    setPreviewTitle(
      file.name || file.url.substring(file.url.lastIndexOf("/") + 1)
    );
  };

  const handleCameraUpload = () => {
    cameraInputRef.current.click();
    setShowUploadOptions(false);
  };

  const handleCameraInputChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      handlePreview(file);
    }
  };

  const handleFolderUpload = () => {
    // Implement folder/gallery upload logic here
    setShowUploadOptions(false);
  };

  const beforeUpload = (file) => {
    setSelectedPhotos([...selectedPhotos, file]);
    return false;
  };

  const showUploadOptionsModal = () => {
    setShowUploadOptions(true);
  };

  const hideUploadOptionsModal = () => {
    setShowUploadOptions(false);
  };

  const uploadButton = (
    <>
      <Button icon={<PlusOutlined />} onClick={showUploadOptionsModal}>
        Upload
      </Button>
      {showUploadOptions && (
        <div className="upload-options-modal">
          <Button
            type="primary"
            icon={<CameraOutlined />}
            onClick={handleCameraUpload}
          >
            Use Camera
          </Button>
          <Button
            type="primary"
            icon={<FolderAddFilled />}
            onClick={handleFolderUpload}
          >
            Select from Folder/Gallery
          </Button>
          <Button onClick={hideUploadOptionsModal}>Cancel</Button>
        </div>
      )}
    </>
  );

  return (
    <>
      <Upload
        listType="picture-card"
        fileList={photoList}
        onPreview={handlePreview}
        name={category ? slugify(category) : "images"}
        onChange={(files) => handelPhotoListChange(files, category)}
        beforeUpload={beforeUpload}
        accept="image/jpeg,image/png"
        multiple
      ></Upload>
      <Modal
        visible={previewVisible}
        title={previewTitle}
        footer={null}
        onCancel={handleCancel}
      >
        <img alt="example" style={{ width: "100%" }} src={previewImage} />
      </Modal>
    </>
  );
};

export default UploadPhotos;
