import React, { useState } from "react";
import { Modal, Upload } from "antd";
import { PlusOutlined } from "@ant-design/icons";
import { slugify } from "../helpers/string-helper";

function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });
}

const UploadPhotos = ({
  photoList,
  handelPhotoListChange,
  category,
  multiple,
}) => {
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewImage, setPreviewImage] = useState("");
  const [previewTitle, setPreviewTitle] = useState("");
  const [selectedPhotos, setSelectedPhotos] = useState([]);

  var showUpload = true;
  if (!multiple) {
    if (photoList.length > 0) {
      showUpload = false;
    }
  }

  const handleCancel = () => {
    setPreviewVisible(false);
    setSelectedPhotos([]);
  };

  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }
    setPreviewImage(file.url || file.preview);
    setPreviewVisible(true);
    setPreviewTitle(
      file.name || file.url.substring(file.url.lastIndexOf("/") + 1)
    );
  };

  const beforeUpload = async (file) => {
    try {
      const watermarkedFile = await applyWatermark(file);
      const updatedPhotoList = [...photoList, watermarkedFile];
      setSelectedPhotos(updatedPhotoList);
      handelPhotoListChange(updatedPhotoList, category);
    } catch (error) {
      console.error("Error applying watermark:", error);
    }
    return false;
  };

  const applyWatermark = (file) => {
    return new Promise((resolve, reject) => {
      const canvas = document.createElement("canvas");
      const context = canvas.getContext("2d");
      const image = new Image();

      image.onload = () => {
        const imageAspectRatio = image.width / image.height;
        const canvasAspectRatio = canvas.width / canvas.height;

        let width;
        let height;
        let offsetX = 0;
        let offsetY = 0;

        if (imageAspectRatio > canvasAspectRatio) {
          width = canvas.width;
          height = width / imageAspectRatio;
          offsetY = (canvas.height - height) / 2;
        } else {
          height = canvas.height;
          width = height * imageAspectRatio;
          offsetX = (canvas.width - width) / 2;
        }

        context.drawImage(image, offsetX, offsetY, width, height);

        // Add your watermark logic here
        const watermarkText = "Second Home Seniors";
        context.font = "20px Arial";
        context.fillStyle = "rgba(255, 255, 255, 0.5)";
        context.textAlign = "center";
        context.textBaseline = "middle";
        context.fillText(watermarkText, canvas.width / 2, canvas.height / 2);

        canvas.toBlob((blob) => {
          const watermarkedFile = new File([blob], file.name, {
            type: file.type,
            lastModified: Date.now(),
          });
          resolve(watermarkedFile);
        }, file.type);
      };

      image.onerror = (error) => reject(error);
      image.src = URL.createObjectURL(file);
      console.log("WaterMark Applied");
    });
  };

  const uploadButton = (
    <div>
      <PlusOutlined />
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  return (
    <>
      <Upload
        listType="picture-card"
        fileList={photoList}
        onPreview={handlePreview}
        name={category ? slugify(category) : "images"}
        onChange={(files) => handelPhotoListChange(files, category)}
        beforeUpload={beforeUpload}
        accept="image/jpeg,image/png"
        multiple
      >
        {showUpload && <>{uploadButton}</>}
      </Upload>
      <Modal
        visible={previewVisible}
        title={previewTitle}
        footer={null}
        onCancel={handleCancel}
      >
        <img alt="example" style={{ width: "100%" }} src={previewImage} />
      </Modal>
    </>
  );
};

export default UploadPhotos;
