import React, { useState, useRef, useEffect } from "react";
import { Modal, Upload, Button } from "antd";
import { CameraOutlined } from "@ant-design/icons";
import { slugify } from "../helpers/string-helper";
import Webcam from "react-webcam";

const isMobileDevice = () => {
  const mobileDeviceRegex = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i;
  return mobileDeviceRegex.test(navigator.userAgent);
};

const UploadPhotosCamera = ({
  photoList = [],
  handelPhotoListChange,
  category,
  multiple,
}) => {
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewImage, setPreviewImage] = useState("");
  const [previewTitle, setPreviewTitle] = useState("");
  const webcamRef = useRef(null);
  const videoRef = useRef(null); // Reference to the video element
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    setIsMobile(isMobileDevice());
  }, []);

  var showUpload = true;
  if (!multiple) {
    if (photoList.length > 0) {
      showUpload = false;
    }
  }

  const handleCancel = () => {
    setPreviewVisible(false);
  };

  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }

    const fileName =
      file.name ||
      (file.url && file.url.substring(file.url.lastIndexOf("/") + 1)) ||
      "";

    setPreviewImage(file.url || file.preview);
    setPreviewVisible(true);
    setPreviewTitle(fileName);
  };

  const handleCameraCapture = () => {
    // Get the video element from the webcamRef
    const videoElement = webcamRef.current.video;
    // Create a canvas element to capture the snapshot
    const canvas = document.createElement("canvas");
    canvas.width = videoElement.videoWidth;
    canvas.height = videoElement.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
    const imageSrc = canvas.toDataURL("image/png"); // Convert the canvas snapshot to data URL

    // Create a new File object from the data URL
    const imageFile = dataURLtoFile(imageSrc, "capturedImage.png");

    // Create a copy of the current photoList and add the captured image to it
    const updatedPhotoList = [...photoList, imageFile];
    // Call the handelPhotoListChange to update the photoList state
    handelPhotoListChange(updatedPhotoList, category);
  };

  const dataURLtoFile = (dataURL, filename) => {
    const arr = dataURL.split(",");
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
    return new File([u8arr], filename, { type: mime });
  };

  const beforeUpload = async (file) => {
    return false;
  };

  const uploadButton = (
    <div onClick={handleCameraCapture}>
      <CameraOutlined />
      <div style={{ marginTop: 8 }}>Camera</div>
    </div>
  );

  return (
    <>
      {isMobile ? (
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/png"
          videoConstraints={{ facingMode: "environment" }}
        />
      ) : (
        <Upload
          listType="picture-card"
          fileList={photoList}
          onPreview={handlePreview}
          name={category ? slugify(category) : "images"}
          onChange={(files) => handelPhotoListChange(files, category)}
          beforeUpload={beforeUpload}
          accept="image/jpeg,image/png"
          multiple
        >
          {showUpload && <>{uploadButton}</>}
        </Upload>
      )}

      <Modal
        visible={previewVisible}
        title={previewTitle}
        footer={null}
        onCancel={handleCancel}
      >
        <img alt="example" style={{ width: "100%" }} src={previewImage} />
      </Modal>
    </>
  );
};

export default UploadPhotosCamera;
