import React, { useEffect, useState } from "react";
import axios from "axios";
const HomeList = () => {
  const [homes, setHomes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredHomes, setFilteredHomes] = useState([]);
  useEffect(() => {
    fetchData();
  }, []);
  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://api.secondhomeseniors.com/items/homes/"
      );
      setHomes(response.data.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };
  useEffect(() => {
    const filteredData = homes.filter((home) => {
      const searchFields = [home.city, home.zipCode, home.country];
      return searchFields.some(
        (field) => field && field.toLowerCase().includes(searchTerm)
      );
    });
    setFilteredHomes(filteredData);
  }, [homes, searchTerm]);
  const handlePlaceSelect = (place) => {
    const address = place.formatted_address;
    console.log("Selected place:", address);
    setSearchTerm(address);
  };
  const autocomplete = (inputElement) => {
    const options = {
      types: ["(cities)"],
    };
    const autoComplete = new window.google.maps.places.Autocomplete(
      inputElement,
      options
    );
    autoComplete.addListener("place_changed", () => {
      const place = autoComplete.getPlace();
      handlePlaceSelect(place);
    });
  };
  useEffect(() => {
    if (window.google && window.google.maps) {
      const inputElement = document.getElementById("autocomplete-input");
      autocomplete(inputElement);
    } else {
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyA6eaMRrfLzlYUe-H1sL6D9tkVXjS6O7BQ&libraries=places`;
      script.onload = () => {
        const inputElement = document.getElementById("autocomplete-input");
        autocomplete(inputElement);
      };
      document.body.appendChild(script);
    }
  }, []);
  return (
    <div>
      <h1>Home List</h1>
      <input
        type="text"
        id="autocomplete-input"
        placeholder="Search by city, zip code, or country"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul>
        {filteredHomes.map((home) => (
          <li key={home.id}>
            <p>City: {home.city}</p>
            <p>Country: {home.country}</p>
            <p>Zip Code: {home.zipCode}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};
export default HomeList;
