import { Col, Row } from "antd";
import Search from "antd/lib/transfer/search";
import React, { useState } from "react";
import { usePlacesAutocomplete } from "react-places-autocomplete";

const main_Search = () => {
  const [address, setAddress] = useState("");

  const {
    suggestions: { data },
    setValue,
  } = usePlacesAutocomplete({
    requestOptions: {
      /* Define your options here, such as country or location biasing */
    },
  });

  const handleInputChange = (e) => {
    setValue(e.target.value);
    setAddress(e.target.value);
  };

  const handleSelectSuggestion = (suggestion) => {
    setAddress(suggestion);
  };

  return (
    <div>
      <input
        type="text"
        value={address}
        onChange={handleInputChange}
        placeholder="City, ZIP, School, Address, Neighborhood"
      />
      <ul>
        {data.map((suggestion) => (
          <li
            key={suggestion.place_id}
            onClick={() => handleSelectSuggestion(suggestion.description)}
          >
            {suggestion.description}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default main_Search;
