import React, { useState, useRef, useEffect } from "react";
import { Modal, Upload, Button } from "antd";
import { CameraOutlined } from "@ant-design/icons";
import { slugify } from "../helpers/string-helper";
import Webcam from "react-webcam";

const isMobileDevice = () => {
  const mobileDeviceRegex = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i;
  return mobileDeviceRegex.test(navigator.userAgent);
};
const getBase64 = (file) =>
  new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

const UploadPhotosCamera = ({
  photoList = [],
  handelPhotoListChange,
  category,
  multiple,
}) => {
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewImage, setPreviewImage] = useState("");
  const [previewTitle, setPreviewTitle] = useState("");
  const [capturedImage, setCapturedImage] = useState(null);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    setIsMobile(isMobileDevice());
  }, []);

  const handleCancel = () => {
    setPreviewVisible(false);
  };

  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }

    const fileName =
      file.name ||
      (file.url && file.url.substring(file.url.lastIndexOf("/") + 1)) ||
      "";

    setPreviewImage(file.url || file.preview);
    setPreviewVisible(true);
    setPreviewTitle(fileName);
  };

  const handleCameraCapture = () => {
    if (isMobile) {
      const constraints = {
        video: { facingMode: "environment" },
        audio: false,
      };

      navigator.mediaDevices
        .getUserMedia(constraints)
        .then((stream) => {
          const videoTrack = stream.getVideoTracks()[0];
          const imageCapture = new ImageCapture(videoTrack);

          imageCapture
            .takePhoto()
            .then((blob) => {
              const capturedImageUrl = URL.createObjectURL(blob);
              setCapturedImage(capturedImageUrl);
              stream.getTracks().forEach((track) => track.stop());
            })
            .catch((error) => {
              console.error("Error capturing image: ", error);
            });
        })
        .catch((error) => {
          console.error("Error accessing camera: ", error);
        });
    }
  };

  const handleSaveCapture = () => {
    if (capturedImage) {
      // Convert the captured image URL to a File object
      const fileName = "capturedImage.png";
      fetch(capturedImage)
        .then((res) => res.blob())
        .then((blob) => {
          const file = new File([blob], fileName, { type: "image/png" });

          // Create a copy of the current photoList and add the captured image to it
          const updatedPhotoList = [...photoList, file];
          // Call the handelPhotoListChange to update the photoList state
          handelPhotoListChange(updatedPhotoList, category);
        })
        .catch((error) => {
          console.error("Error saving captured image: ", error);
        });
    }
  };
  const beforeUpload = async (file) => {
    return false;
  };

  return (
    <>
      {isMobile ? (
        <div>
          <img
            alt="captured"
            style={{ maxWidth: "100%" }}
            src={capturedImage}
          />
          <Button onClick={handleSaveCapture}>Save Capture</Button>
        </div>
      ) : (
        <div>
          <img
            alt="captured"
            style={{ maxWidth: "100%" }}
            src={capturedImage}
          />
          <Button onClick={handleSaveCapture}>Save Capture</Button>
        </div>
      )}

      <Modal
        visible={previewVisible}
        title={previewTitle}
        footer={null}
        onCancel={handleCancel}
      >
        <img alt="example" style={{ width: "100%" }} src={previewImage} />
      </Modal>
    </>
  );
};

export default UploadPhotosCamera;
