import React, { useEffect, useState } from "react";
import axios from "axios";

const HomeList = () => {
  const [homes, setHomes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredHomes, setFilteredHomes] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://api.secondhomeseniors.com/items/homes/"
      );
      setHomes(response.data.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    const filteredData = homes.filter((home) => {
      const searchFields = [home.city, home.zipCode, home.country];
      return searchFields.some(
        (field) =>
          field && field.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setFilteredHomes(filteredData);
  }, [homes, searchTerm]);

  const handlePlaceSelect = (place) => {
    const address = place.formatted_address;
    console.log("Selected place:", address);
    setSearchTerm(address);
  };

  const autocomplete = (inputElement) => {
    const options = {
      types: ["(regions)"], // Use (regions) instead of (cities) to get broader results
    };
    const autoComplete = new window.google.maps.places.Autocomplete(
      inputElement,
      options
    );
    autoComplete.setFields(["address_components", "types"]); // Request additional fields for categorization
    autoComplete.addListener("place_changed", () => {
      const place = autoComplete.getPlace();
      handlePlaceSelect(place);
    });
  };

  useEffect(() => {
    if (window.google && window.google.maps) {
      const inputElement = document.getElementById("autocomplete-input");
      autocomplete(inputElement);
    } else {
      // Load the Google Maps JavaScript API
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyA6eaMRrfLzlYUe-H1sL6D9tkVXjS6O7BQ&libraries=places`;
      script.onload = () => {
        const inputElement = document.getElementById("autocomplete-input");
        autocomplete(inputElement);
      };
      document.body.appendChild(script);
    }
  }, []);

  const categorizeSuggestions = (suggestions) => {
    const categorizedSuggestions = {
      city: [],
      neighbors: [],
      landmarks: [],
    };

    suggestions.forEach((suggestion) => {
      const types = suggestion.types || [];

      if (types.includes("locality")) {
        categorizedSuggestions.city.push(suggestion.description);
      } else if (types.includes("neighborhood")) {
        categorizedSuggestions.neighbors.push(suggestion.description);
      } else {
        categorizedSuggestions.landmarks.push(suggestion.description);
      }
    });

    return categorizedSuggestions;
  };

  return (
    <div>
      <h1>Home List</h1>
      <input
        type="text"
        id="autocomplete-input"
        placeholder="Search by city, zip code, or country"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul>
        {Object.entries(categorizeSuggestions(filteredHomes)).map(
          ([category, suggestions]) => (
            <li key={category}>
              <strong>{category}</strong>
              <ul>
                {suggestions.map((suggestion) => (
                  <li key={suggestion}>{suggestion}</li>
                ))}
              </ul>
            </li>
          )
        )}
      </ul>
    </div>
  );
};

export default HomeList;

// "https://api.secondhomeseniors.com/items/homes/"
