import React, { useState } from "react";
import { Modal, Upload, message } from "antd";

import { UploadOutlined, PlusOutlined } from "@ant-design/icons";
import Config from "../config";
import { slugify } from "../helpers/string-helper";




function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
  });
}


const UploadPhotos = ({ photoList, handelPhotoListChange, category, multiple }) => {
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewImage, setPreviewImage] = useState('');
  const [previewTitle, setPreviewTitle] = useState("");
  var showUpload = true;
  if (!multiple) {
    if (photoList.length > 0) {
      showUpload = false;
    }

  }


  const handleCancel = () => setPreviewVisible(false);

  const handlePreview = async file => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }
    setPreviewImage(file.url || file.preview);
    setPreviewVisible(true);
    setPreviewTitle(file.name || file.url.substring(file.url.lastIndexOf('/') + 1))
  };



  const beforeUpload = file => {
    
    return false;
  }




  const uploadButton = (
    <div>
      <PlusOutlined />
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  return (
    <>
      <Upload
        listType="picture-card"
        fileList={photoList}
        onPreview={handlePreview}
        name={category ? slugify(category) : "images"}
        onChange={(files) => handelPhotoListChange(files, category)}
        beforeUpload={beforeUpload}
        accept="image/jpeg,image/png"
      >
        {showUpload && uploadButton}
      </Upload>
      <Modal
        visible={previewVisible}
        title={previewTitle}
        footer={null}
        onCancel={handleCancel}
      >
        <img alt="example" style={{ width: '100%' }} src={previewImage} />
      </Modal>
    </>
  );

};


export default UploadPhotos;
