import React, { useEffect, useState } from "react";
import axios from "axios";
import GooglePlacesAutocomplete from "react-google-places-autocomplete";
import config from "../../../config";
const HomeList = () => {
  const [homes, setHomes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredHomes, setFilteredHomes] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://api.secondhomeseniors.com/items/homes/"
      );
      setHomes(response.data.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    const filteredData = homes.filter((home) => {
      const searchFields = [home.city, home.zipCode, home.country];
      return searchFields.some(
        (field) =>
          field && field.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setFilteredHomes(filteredData);
  }, [homes, searchTerm]);

  const handlePlaceSelect = (place) => {
    const address = place.description;
    console.log("Selected place:", address);
    setSearchTerm(address.toString());
  };

  return (
    <div>
      <h1>Home List</h1>
      <GooglePlacesAutocomplete
        apiKey={config.googleMapsAPIkey}
        autocompletionRequest={{
          types: ["(cities)"],
        }}
        selectProps={{
          value: searchTerm,
          onChange: ({ value }) => setSearchTerm(value),
          placeholder: "Search by city, zip code, or country",
        }}
        onLoadFailed={(error) =>
          console.error("Error loading Google Maps API", error)
        }
        debounce={300}
        onSelect={handlePlaceSelect}
      />
      <ul>
        {filteredHomes.map((home) => (
          <li key={home.id}>
            <p>City: {home.city}</p>
            <p>Country: {home.country}</p>
            <p>Zip Code: {home.zipCode}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HomeList;

// "https://api.secondhomeseniors.com/items/homes/"
