import { Col, Input, Row } from "antd";
import React, { useState, useEffect } from "react";
import "../../../styles/autoComplete-search.css";

function MainSearch() {
  const [autoComplete, setAutoComplete] = useState(false);
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    const script = document.createElement("script");
    script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyA6eaMRrfLzlYUe-H1sL6D9tkVXjS6O7BQ&libraries=places`;
    script.async = true;
    script.defer = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const handleAutoCompleteView = (data) => {
    if (data.trim() !== "") {
      setAutoComplete(true);
      fetchCity(data);
    } else {
      setAutoComplete(false);
      setSuggestions([]);
    }
  };

  const fetchCity = (input) => {
    const autocompleteService = new window.google.maps.places.AutocompleteService();
    autocompleteService.getPlacePredictions(
      {
        input: input,
        types: ["(cities)"], // Restrict results to cities
        componentRestrictions: { country: "us" }, // Restrict results to USA
      },
      (predictions, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
          const simplifiedSuggestions = predictions.map((prediction) => {
            // Extract only the city name
            const cityName = prediction.structured_formatting.main_text;
            return { ...prediction, description: cityName };
          });
          setSuggestions(simplifiedSuggestions);
        }
      }
    );
  };
  return (
    <Row>
      <Input
        type="text"
        onChange={(e) => {
          handleAutoCompleteView(e.target.value);
        }}
      />
      {autoComplete && (
        <div className="auto_complete">
          <ul>
            <li>
              <section className="suggestion_City">
                <h2>CITIES</h2>
                {suggestions.map((suggestion) => (
                  <div key={suggestion.place_id}>
                    <p>{suggestion.description}</p>
                  </div>
                ))}
              </section>
            </li>
          </ul>
        </div>
      )}
    </Row>
  );
}

export default MainSearch;
