    setConfirmModalVisible(true);
  };

  const sortAZ = () => {
    let NewHomes = homes.sort(function(a, b) {
      if (a.name.toLowerCase() < b.name.toLowerCase()) {
        return -1;
      }
      if (a.name.toLowerCase() > b.name.toLowerCase()) {
        return 1;
      }
      return 0;
    });
    setHomes(NewHomes);
    setHomeLoading(true);
  };
  const sortZA = () => {
    let NewHomes = homes.sort(function(a, b) {
      if (a.name.toLowerCase() > b.name.toLowerCase()) {
        return -1;
      }
      if (a.name.toLowerCase() > b.name.toLowerCase()) {
        return 1;
      }
      return 0;
    });
    setHomes(NewHomes);
    setHomeLoading(true);
  };
  const sortLowest = () => {
    let NewHomes = homes.sort(function(a, b) {
      return a.min_budget - b.min_budget;
