import React, { useEffect, useState } from "react";
import axios from "axios";

const HomeList = () => {
  const [homes, setHomes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredHomes, setFilteredHomes] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://api.secondhomeseniors.com/items/homes/"
      );
      setHomes(response.data.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    const filteredData = homes.filter((home) => {
      const searchFields = [
        home.city,
        home.address_line_1,
        home.address_line_2,
      ];
      return searchFields.some((field) =>
        field.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setFilteredHomes(filteredData);
  }, [homes, searchTerm]);

  return (
    <div>
      <h1>Home List</h1>
      <input
        type="text"
        placeholder="Search by city, zip code, or country"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul>
        {filteredHomes.map((home) => (
          <li key={home.id}>
            <p>City: {home.city}</p>
            <p>Country: {home.address_line_1}</p>
            <p>Zip Code: {home.address_line_2}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};
export default HomeList;
// "https://api.secondhomeseniors.com/items/homes/"
