import React, { useEffect, useState } from "react";
import axios from "axios";
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng,
} from "react-google-autocomplete";

const HomeList = () => {
  const [homes, setHomes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredHomes, setFilteredHomes] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://api.secondhomeseniors.com/items/homes/"
      );
      setHomes(response.data.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    const filteredData = homes.filter((home) => {
      const searchFields = [home.city, home.zipCode, home.country];
      return searchFields.some((field) =>
        field.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setFilteredHomes(filteredData);
  }, [homes, searchTerm]);

  const handlePlaceSelect = async (address) => {
    try {
      const results = await geocodeByAddress(address);
      const latLng = await getLatLng(results[0]);
      console.log("Selected place:", address);
      console.log("Latitude:", latLng.lat);
      console.log("Longitude:", latLng.lng);
      setSearchTerm(address);
    } catch (error) {
      console.error("Error selecting place:", error);
    }
  };

  return (
    <div>
      <h1>Home List</h1>
      <PlacesAutocomplete
        value={searchTerm}
        onChange={(value) => setSearchTerm(value)}
        onSelect={handlePlaceSelect}
        placeholder="Search by city, zip code, or country"
      />
      <ul>
        {filteredHomes.map((home) => (
          <li key={home.id}>
            <p>City: {home.city}</p>
            <p>Country: {home.country}</p>
            <p>Zip Code: {home.zipCode}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HomeList;

// "https://api.secondhomeseniors.com/items/homes/"
