import { Col, Row } from "antd";
import React from "react";
import {
  GoogleMap,
  useLoadScript,
  StandaloneSearchBox,
} from "google-maps-react";
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng,
} from "react-google-places-autocomplete";

import config from "../../../config";

const main_Search = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: config.googleMapsAPIkey, // Replace with your Google Maps API key
  });

  const handleSelect = async (address) => {
    try {
      const results = await geocodeByAddress(address);
      const latLng = await getLatLng(results[0]);
      console.log("Selected location:", latLng);
      // Do something with the selected location, such as updating state or making API calls
    } catch (error) {
      console.error("Error selecting location:", error);
    }
  };

  return (
    <div>
      {loadError ? (
        <div>Error loading maps</div>
      ) : !isLoaded ? (
        <div>Loading maps</div>
      ) : (
        <GoogleMap
          google={window.google}
          zoom={14}
          center={{ lat: 37.7749, lng: -122.4194 }}
        >
          <StandaloneSearchBox>
            <PlacesAutocomplete
              onSelect={({ description }) => handleSelect(description)}
              searchOptions={{
                types: ["(cities)", "administrative_area_level_2"],
              }}
            >
              {({
                getInputProps,
                suggestions,
                getSuggestionItemProps,
                loading,
              }) => (
                <div>
                  <input
                    {...getInputProps({ placeholder: "City, Zip, School" })}
                  />
                  <div>
                    {loading ? <div>Loading suggestions...</div> : null}
                    {suggestions.map((suggestion) => (
                      <div
                        key={suggestion.place_id}
                        {...getSuggestionItemProps(suggestion)}
                        style={{ cursor: "pointer" }}
                      >
                        {suggestion.description}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </PlacesAutocomplete>
          </StandaloneSearchBox>
        </GoogleMap>
      )}
    </div>
  );
};

function mapStateToProps(state) {
  return {};
}

function mapDispatchToProps(dispatch) {
  return {
    signin: (email, password) => dispatch(signin(email, password)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(DashboardSearch);
